<script>
export default {
  name: 'nav-bar',
  props: {
    currentRoute: {
      type: Object,
      required: true
    }
  },
  computed: {
    currentPath() {
      if (this.currentRoute.name === 'index') return 'home';
      const splitPath = this.currentRoute.path.split("/");
      if (splitPath.length > 1) {
        return splitPath[1];
      }
      return '';
    }
  }
};
